css:
.salary_voucher{}
.salary_voucher .page_title{
	margin-bottom: 30px;
}
.salary_voucher .page_title .title{
	line-height: 1.4;
	font-size: 30px;
	text-align: center;
}
.salary_voucher .attendance_sec,
.salary_voucher .summary_sec,
.salary_voucher .personal_info_sec{
	margin-bottom: 30px
}
.salary_voucher .personal_info_sec .info_container{}
.salary_voucher .personal_info_sec .info_container table{
	width: 100%;
}
.salary_voucher .personal_info_sec .info_container table th,
.salary_voucher .personal_info_sec .info_container table td{
	line-height: 1.6;
	font-size: 16px;
}
.salary_voucher .personal_info_sec .info_container table th{}
.salary_voucher .personal_info_sec .info_container table td{}
.salary_voucher .personal_info_sec .person_img{}
.salary_voucher .personal_info_sec .person_img img{
	width: 200px;
	height: 200px;
	-webkit-object-fit: cover;
	object-fit: cover;
}
.salary_voucher .summary_sec{}
.salary_voucher .summary_sec .total_summary table{
	background-color: #f2f2f2;
}
